
function setCheckedStart(value) {
    var stars = document.getElementsByClassName('block-rating-start');
    for(var i = 0; i <= value; i++) {
        stars[i].className = 'icon fa fa-star block-rating-start checked';
    }
}

function setUnCheckedStart(value) {
    var stars = document.getElementsByClassName('block-rating-start');
    for(var i = 0; i <= value; i++) {
        stars[i].className = 'icon fa fa-star-o block-rating-start';
    }
}

var stars = document.getElementsByClassName('block-rating-start');

stars.forEach(element => {
    element.addEventListener('mouseover', (event)=> {
        setCheckedStart(element.dataset.block_rating);
    })
    element.addEventListener('mouseleave', (event)=> {
        setUnCheckedStart(element.dataset.block_rating);
    })
});